        abort() {
            let req = this._req;
            if (req) {
                // 设置outFlag，会中止回调函数的回调
                req.outFlag = true;
                if (req.xhr) {
                    // xhr 可以原声支持 中止
                    req.xhr.abort();
                    req.xhr = null;
                }
                delete this._req;
            }
            return this;
        }

