        timeout(time, callback) {
            setTimeout(() => {
                let req = this._req;
                if (req) {
                    // 超时 设置中止
                    this.abort();
                    // 出发超时事件
                    this.emit("timeout", req);
                }
            }, time);
            callback && this.on("timeout", callback);
            return this;
        }

