        send(param, over) {
            if (this._req) {
                // 存在 _req
                if (!over) {
                    // 不覆盖，取消本次发送
                    return this;
                }
                // 中止当前的
                this.abort();
            }

            // 制造 req
            let req = (this._req = {
                root: this,
                async: this.async
            });

            if (!this.async) {
                // 同步
                return requestSend.call(this, param || {}, req);
            }
            // 异步，settime 部分参数可以后置设置生效
            setTimeout(requestSend.bind(this, param || {}, req), 1);
            return this;
        }
    }

